import com.flexive.shared.scripting.groovy.*
import com.flexive.shared.value.*
import com.flexive.shared.security.*
import com.flexive.shared.*
import com.flexive.shared.structure.*

new GroovyTypeBuilder().person(description: new FxString("Person"),                     
        acl: CacheAdmin.environment.getACL(ACLCategory.STRUCTURE.defaultId),
        multilang: true) {
}
// we "reload" the "Person" type to show the means of adding properties using the
// GroovyTypeBuilder after type creation.
def builder = new GroovyTypeBuilder("Person")                                           
builder {
    firstname(dataType: FxDataType.String1024,                                          
            multilang: false,
            description: new FxString(FxLanguage.ENGLISH, "A person's first name"),     
            multiplicity: FxMultiplicity.MULT_0_N)
}

new GroovyTypeBuilder().anotherperson(description: new FxString("Anotherperson"),       
        acl: CacheAdmin.environment.getACL(ACLCategory.STRUCTURE.defaultId),
        multilang: true) {
            anotherfirstname(assignment: "PERSON/FIRSTNAME",                            
            description: new FxString(FxLanguage.ENGLISH, "Another's first name"))      
}

def name = ""
def type = CacheAdmin.getEnvironment().getType("person")
FxPropertyAssignment propAssign = type.getPropertyAssignment("PERSON/FIRSTNAME")
name = propAssign.getLabel()
EJBLookup.getAssignmentEngine().removeAssignment(propAss.getId(), false, false);        


FxProperty prop = CacheAdmin.getEnvironment().getProperty("firstname");                 
return "Removed assigned (label): " + name + "; Retained label for property: "
    + String.valueOf(prop.getLabel())
